#ifndef ROOT_RVersion
#define ROOT_RVersion

/* Version information automatically generated by installer. */

/*
 * These macros can be used in the following way:
 *
 *    #if ROOT_VERSION_CODE >= ROOT_VERSION(2,23,4)
 *       #include <newheader.h>
 *    #else
 *       #include <oldheader.h>
 *    #endif
 *
*/

#define ROOT_RELEASE "5.27/06a"
#define ROOT_RELEASE_DATE "Oct 13 2010"
#define ROOT_RELEASE_TIME "13:05:52"
#define ROOT_SVN_REVISION 36203
#define ROOT_SVN_BRANCH "branches/v5-27-06-patches"
#define ROOT_VERSION_CODE 334598
#define ROOT_VERSION(a,b,c) (((a) << 16) + ((b) << 8) + (c))

#endif
