#ifndef ROOT_RVersion
#define ROOT_RVersion

/* Version information automatically generated by installer. */

/*
 * These macros can be used in the following way:
 *
 *    #if ROOT_VERSION_CODE >= ROOT_VERSION(2,23,4)
 *       #include <newheader.h>
 *    #else
 *       #include <oldheader.h>
 *    #endif
 *
*/

#define ROOT_RELEASE "5.26/00a"
#define ROOT_RELEASE_DATE "Jan 19 2010"
#define ROOT_RELEASE_TIME "11:17:17"
#define ROOT_SVN_REVISION 32048
#define ROOT_SVN_BRANCH "branches/v5-26-00-patches"
#define ROOT_VERSION_CODE 334336
#define ROOT_VERSION(a,b,c) (((a) << 16) + ((b) << 8) + (c))

#endif
